def process_user_query(query_string):
    language=[]
    user_want=query_string.split(' ')
    user_want=user_want[0].lower()

    if user_want=="language":
        query_string=query_string.split(' ')
        i=query_string.index("Languages")
        i=i+1

        for lang in languages:
            if query_string[i]=="Edit":
                break
            else:
                if query_string[i]==lang:
                    language.append(query_string[i])
                    i=i+1
                else:
                    break
        return language

    elif user_want=="populations":
        population=[]
        query_string=query_string.split(' ')
        city=query_string.index("(disambiguation).")
        population.append("City:" + query_string[city+1])
        i=query_string.index("Population")
        ix=query_string.index("Demonym(s)")
        i=i+2
        for pop in query_string[i:ix]:
            population.append(pop)
        return population

    else:
        pass

